import React from "react";
import { Link } from 'react-router-dom';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation} from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation'
import Product from "./item/Product";
import News from "./item/News";

const ListItem = ({dataset, role, swiperButton = "default-swiper", direction = "horizontal", spaceBetween = 20, slidesPerView = 4 }) => {

    const formatPrice = (price) => {
        // Sử dụng Intl.NumberFormat để định dạng số với dấu phân cách hàng nghìn
        return new Intl.NumberFormat('vi-VN').format(price);
    };
    

    return (
        <div className="listProducts">
            <Swiper
                direction={direction}
                modules={[Navigation]}
                spaceBetween={spaceBetween}
                slidesPerView={slidesPerView}
                navigation={{
                    prevEl: `.${swiperButton}-prev`,
                    nextEl: `.${swiperButton}-next`,
                }}
                breakpoints={{
                    0: { // Với mọi kích thước màn hình
                        slidesPerView: 2, // Điện thoại, chỉ hiện 1 slide
                    },
                    776: { // Màn hình từ 640px trở lên (tablet trở lên)
                        slidesPerView: 3, // Hiện 2 slide như mặc định
                    },
                    1032: {
                        slidesPerView: 4,
                    }
                    // 1200: {
                    //     slidesPerView:5,
                    // }
                }}
            >
            {dataset && dataset.map(data, index => (
                <SwiperSlide key={index}>
                    {role === "product" ? (
                        <Product data={data} />
                    ) : (
                        <News data={data} />
                    )}
                </SwiperSlide>
            ))}
            </Swiper>
        </div>
    )   
}

export default ListItem
