import React from "react";
import { Link } from 'react-router-dom';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';

const ListProducts = ({ dataset, swiper = true, swiperButton = "default-swiper", direction = "horizontal", spaceBetween = 20, slidesPerView = 4 }) => {
    
    const formatPrice = (price) => {
        // Sử dụng Intl.NumberFormat để định dạng số với dấu phân cách hàng nghìn
        return new Intl.NumberFormat('vi-VN').format(price);
    };
    
    return (
        <div className="listProducts">
            {swiper ? (
                <Swiper
                    direction={direction}
                    modules={[Navigation]}
                    spaceBetween={spaceBetween}
                    slidesPerView={slidesPerView}
                    navigation={{
                        prevEl: `.${swiperButton}-prev`,
                        nextEl: `.${swiperButton}-next`,
                    }}
                    style={{ height: direction === "vertical" ? "500px" : "auto" }}
                    breakpoints={{
                        0: { slidesPerView: 2 },
                        776: { slidesPerView: 3 },
                        1032: { slidesPerView: 4 }
                    }}
                >
                    {dataset && dataset.map(data => (
                        <SwiperSlide key={data.product_id}>
                            <div className="product-item wrp-effect-scale wrp-effect-boxshadow">
                                <div className="inner-image">
                                    <div className="ratio-1-1">
                                        <Link to={`/product/${data.product_id}`} title={data.product_name}>
                                            <img className="img-fluid" src={data.product_image_url} alt={data.product_name} />
                                        </Link>
                                    </div>
                                </div>
                                <div className="product-item__content">
                                    <h4 className="product-name">
                                        <Link to={`/product/${data.product_id}`}>
                                            {data.product_name}
                                        </Link>
                                    </h4>
                                    <div className="product-price">
                                        <span className="price-amount">
                                            {formatPrice(data.price)}
                                            <span className="currency-symbol">VND</span>
                                        </span>
                                        {data.old_price && 
                                            <span className="price-amount old-price">
                                                {formatPrice(data.old_price)}
                                                <span className="currency-symbol">VND</span>
                                            </span>
                                        }
                                    </div>
                                </div>
                            </div>
                        </SwiperSlide>
                    ))}
                </Swiper>
            ) : (
                <div className="product-list">
                    {dataset && dataset.map(data => (
                        <div key={data.product_id} className="product-item wrp-effect-scale wrp-effect-boxshadow">
                            <div className="inner-image">
                                <div className="ratio-1-1">
                                    <Link to={`/product/${data.product_id}`} title={data.product_name}>
                                        <img className="img-fluid" src={data.product_image_url} alt={data.product_name} />
                                    </Link>
                                </div>
                            </div>
                            <div className="product-item__content">
                                <h4 className="product-name">
                                    <Link to={`/product/${data.product_id}`}>
                                        {data.product_name}
                                    </Link>
                                </h4>
                                <div className="product-price">
                                    <span className="price-amount">
                                        {formatPrice(data.price)}
                                        <span className="currency-symbol">VND</span>
                                    </span>
                                    {data.old_price && 
                                        <span className="price-amount old-price">
                                            {formatPrice(data.old_price)}
                                            <span className="currency-symbol">VND</span>
                                        </span>
                                    }
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );   
}

export default ListProducts;
