import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation } from "swiper/modules";
import 'swiper/css';
import 'swiper/css/navigation';
import News from "./News";

const Relative = ({newsTitle, newsDatas, role}) => {
    return (
        <div className="news">
            <div className="news-title">
                <h3>
                    {newsTitle}
                </h3>
            </div>
            <Swiper
                modules={[Navigation]}
                spaceBetween={20}
                slidesPerView={4}
                navigation
                breakpoints={{
                    0: { // Với mọi kích thước màn hình
                        slidesPerView: 2, // Điện thoại, chỉ hiện 1 slide
                    },
                    776: { // Màn hình từ 640px trở lên (tablet trở lên)
                        slidesPerView: 3,
                    },
                    1032: {
                        slidesPerView: 4,
                    }
                    // 1200: {
                    //     slidesPerView:5,
                    // }
                }}
            >
                {newsDatas && newsDatas.map(newsData => (
                    <SwiperSlide key={newsData.news_id}>
                        <News newsData={newsData}/>
                    </SwiperSlide>
                ))}
            </Swiper>
        </div>
    )
}

export default Relative
