import React from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation} from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation'

const ListProducts = ({title, dataset, theme}) => {
    return (
        <div className="listProducts">
            <div className="title-link">
                <div>
                    {theme}
                    <h3 className="title-section d-inline">{title}</h3>
                </div>
                {/* 2 nút navigator ở đây */}
                {/* {isSeeAll && <a href="">Xem tất cả</a>} */}
            </div>
            <Swiper
                modules={[Navigation]}
                spaceBetween={0}
                slidesPerView={1}
                loop
                navigation
                breakpoints={{
                    0: { // Với mọi kích thước màn hình
                        slidesPerView: 1, // Điện thoại, chỉ hiện 1 slide
                    },
                    768: { // Màn hình từ 640px trở lên (tablet trở lên)
                        slidesPerView: 2, // Hiện 2 slide như mặc định
                    },
                }}
            >
            </Swiper>
            {dataset.map(data => (
                <SwiperSlide>
                    <div key={data.product_id} className="product-item">
                        <div className="product-item__image">
                            <div className="ratio-1-1">
                                <a href="" title={data.product_name}>
                                    <img className="img-fluid" src={data.product_img}/>
                                </a>
                            </div>
                        </div>
                        <div className="product-item__content">
                            <h4 className="product-name">
                                <a href="">
                                    {data.product_name}
                                </a>
                            </h4>
                            <div className="product-price">
                                <span className="price-amount">
                                    {data.product_price}
                                    <span className="currency-symbol">VND</span>
                                </span>
                                {data.product_old_price && 
                                    <span className="price-amount old-price">
                                        {data.product_old_price}
                                        <span className="currency-symbol">VND</span>
                                    </span>
                                }
                            </div>
                        </div>
                    </div>
                </SwiperSlide>
            ))}
        </div>
    )   
}

export default ListProducts
