import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

const Navbar = () => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [isOpenSideBar, setIsOpenSideBar] = useState(false);
    const [categories, setCategories] = useState([]);

    const toggleMenu = (event) => {
        event.preventDefault();
        setIsMenuOpen(!isMenuOpen);
    };

    const openSidebar = (event) => {
        event.preventDefault();
        setIsOpenSideBar(!isOpenSideBar)
    };

    const closeSidebar = (event) => {
        event.preventDefault();
        setIsOpenSideBar(false);
    };

    const buildCategoryTree = useCallback((categories) => {
        const categoryMap = {};
        const tree = [];

        categories.forEach(category => {
            categoryMap[category.id] = { ...category, children: [] };
        });

        categories.forEach(category => {
            if (category.parent_category_id) {
                categoryMap[category.parent_category_id].children.push(categoryMap[category.id]);
            } else {
                tree.push(categoryMap[category.id]);
            }
        });

        return tree;
    }, []);

    useEffect(() => {
        axios.get(`http://localhost:3000/products/categories`)
            .then(response => {
                const tree = buildCategoryTree(response.data);
                setCategories(tree);
            })
            .catch(error => {
                console.log("Lỗi khi gọi API danh mục!", error);
            });
    }, [buildCategoryTree]);

    const renderCategories = (categories) => (
        <ul>
            {categories.map(category => (
                <li key={category.id}>
                    {category.name}
                    {category.children.length > 0 && renderCategories(category.children)}
                </li>
            ))}
        </ul>
    );

    return (
        <div className="nav">
            <div className="container">
                <div className="row">
                    <div className="col-md-3 col-12">
                        <div className="nav-menu">
                            <button className="nav-menu-title" onClick={toggleMenu}>
                                <i className="fa-solid fa-bars me-4"></i>
                                Danh mục
                            </button>
                            <div className={`nav-menu-opt ${isMenuOpen ? "open" : ""}`}>
                                {renderCategories(categories)}
                            </div>
                        </div>
                    </div>
                    <div className="col-md-9 col-12">
                        <div className="nav-container">
                            <button className="btn-nav-mobile" onClick={openSidebar}>
                                <i className="fa-solid fa-bars-staggered"></i>
                            </button>
                            <div className={`back-drop ${isOpenSideBar ? "open" : ""}`} onClick={closeSidebar}></div>
                            <div className={`nav-section ${isOpenSideBar ? "open" : ""}`}>
                                <div className="nav-top">
                                    <span className="nav-top-title">Menu</span>
                                    <button className="close-sidebar effect-rotate icon-close" onClick={closeSidebar}>
                                        <i className="fa-solid fa-xmark"></i>
                                    </button>
                                </div>
                                {/* Nội dung menu sidebar */}
                                <ul>
                                    <li><a href="" className="active">Trang chủ</a></li>
                                    <li><a href="">Giới thiệu</a></li>
                                    <li className="position-relative">
                                        <a href="">
                                            Sản phẩm
                                            <i className="fa-solid fa-chevron-down"></i>
                                        </a>
                                        {/* Danh sách sản phẩm cố định */}
                                        <ul className="nav-menu-list down">
                                            <li><a href="" className="nav-opt">Nồi cơm điện</a></li>
                                            {/* Thêm các mục tương tự */}
                                        </ul>
                                    </li>
                                    <li><a href="">Tin tức</a></li>
                                    <li><a href="">Liên hệ</a></li>
                                </ul>
                            </div>
                        </div>
                        <div className="nav-hotline">
                            <a href="">
                                <i className="fa-solid fa-phone-volume me-3"></i>
                                1900 6680 - 09011916
                            </a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Navbar;
