import React from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const ListProducts = ({ title, dataset, theme }) => {
    return (
        <div className="container">
            <div className="listProducts">
                <div className="title-link">
                    <div>
                        {theme}
                        <h3 className="title-section d-inline">{title}</h3>
                    </div>
                    <div className="swiper-navigation">
                        <div className="swiper-button-prev"></div>
                        <div className="swiper-button-next"></div>
                    </div>
                </div>

                <Swiper
                    modules={[Navigation]}
                    spaceBetween={0} // Khoảng cách giữa các slide
                    slidesPerView={4} // Số lượng slide hiển thị trên mỗi lần
                    navigation={{
                        prevEl: '.swiper-button-prev', // Kết nối nút prev
                        nextEl: '.swiper-button-next', // Kết nối nút next
                    }}
                >
                    <div className="col-lg-3 col-md-3 col-6 mb-4">
                    {dataset.map(data => (
                            <SwiperSlide key={data.product_id}>
                            <div className="product-item">
                                <div className="product-item__image">
                                    <div className="ratio-1-1">
                                        <a href="" title={data.product_name}>
                                            <img className="img-fluid" src={data.product_img} alt={data.product_name} />
                                        </a>
                                    </div>
                                </div>
                                <div className="product-item__content">
                                    <h4 className="product-name">
                                        <a href="">
                                            {data.product_name}
                                        </a>
                                    </h4>
                                    <div className="product-price">
                                        <span className="price-amount">
                                            {data.product_price}
                                            <span className="currency-symbol">VND</span>
                                        </span>
                                        {data.product_old_price && 
                                            <span className="price-amount old-price">
                                                {data.product_old_price}
                                                <span className="currency-symbol">VND</span>
                                            </span>
                                        }
                                    </div>
                                </div>
                            </div>
                            </SwiperSlide>
                    ))}
                    </div>
                </Swiper>
            </div>
        </div>
    );
}

export default ListProducts;
