// FavoriteContext.js
import axios from 'axios';
import React, { createContext, useState, useEffect, useContext } from 'react';
import { AuthContext } from "./AuthContext";

export const FavoriteContext = createContext();

export const FavoriteProvider = ({ children }) => {
    
    const { user } = useContext(AuthContext);

    const [favorites, setFavorites] = useState([]);

    console.log(favorites);

    // Tải danh sách yêu thích khi component mount
    useEffect(() => {
        const getFavorites = async () => {
            if (user) {
                try {
                    const favoritesProduct = await axios.get(`http://localhost:3000/favorites/${user.user_id}`);
                    const isFavorite = favoritesProduct.some(item => item.product_id === dataProducts.product_id);
                    setFavorites(isFavorite);
                } catch (error) {
                    console.error("Error get favorites from API:", error);
                }
            } else {
                const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
                setFavorites(savedFavorites);
            }
        };

        getFavorites();  // Gọi hàm để lấy dữ liệu từ localStorage
    }, [user]);

    // Thêm sản phẩm vào danh sách yêu thích
    const addToFavorites = async (product_id) => {
        if (user) {
            try {
                const response = await axios.post('http://localhost:3000/favorites', {user_id : user.user_id, product_id})
            } catch (error) {
                if (error.response) {
                    console.error(error.response.data.message);
                }
            }
        } else {
            if (!favorites.includes(product_id)) {
                const newFavorites = [...favorites, product_id];
                setFavorites(newFavorites);
                localStorage.setItem('favorites', JSON.stringify(newFavorites));
            }
        }

    };

    // Xóa sản phẩm khỏi danh sách yêu thích
    const removeFavorites = async (product_id) => {
        if (user) {
            try {
                const response = await axios.delete(`http://localhost:3000/favorites/${user.user_id, product_id}`);
            } catch (error) {
                if (error.response) {
                    console.error(error.response.data.message);
                }
            }
        }
        const newFavorites = favorites.filter(item => item !== product_id);
        setFavorites(newFavorites);
        localStorage.setItem('favorites', JSON.stringify(newFavorites));
    };

    return (
        <FavoriteContext.Provider value={{ favorites, addToFavorites, removeFavorites }}>
            {children}
        </FavoriteContext.Provider>
    );
};
