import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation } from "swiper/modules";
import 'swiper/css';
import 'swiper/css/navigation';

const Relative = ({newsTitle, newsDatas}) => {
    return (
        <div className="news">
            <div className="news-title">
                <h3>
                    {newsTitle}
                </h3>
            </div>
            <Swiper
                modules={[Navigation]}
                spaceBetween={20}
                slidesPerView={4}
                navigation
                breakpoints={{
                    0: { // Với mọi kích thước màn hình
                        slidesPerView: 2, // Điện thoại, chỉ hiện 1 slide
                    },
                    776: { // Màn hình từ 640px trở lên (tablet trở lên)
                        slidesPerView: 3,
                    },
                    1032: {
                        slidesPerView: 4,
                    }
                    // 1200: {
                    //     slidesPerView:5,
                    // }
                }}
            >
                {newsDatas && newsDatas.map(newsData => (
                    <SwiperSlide key={newsData.news_id}>
                        <article className="news-item wrp-effect-scale wrp-effect-boxshadow">
                            <div className="inner-image ">
                                <div className="ratio-3-2">
                                    <a href="#">
                                        <img className="img-fluid" src={newsData.news_image} alt="image"/>
                                    </a>
                                </div>
                            </div>
                            <div className="news-content">
                                <h4 className="news-item--title wrp-effect-color">
                                    <a href="#">
                                        {newsData.news_title}
                                    </a>
                                </h4>
                                <div className="news-item--date">
                                    {newsData.news_date}
                                </div>
                                <div className="news-item--description">
                                    {newsData.news_description}
                                </div>
                            </div>
                        </article>
                    </SwiperSlide>
                ))}
            </Swiper>
        </div>
    )
}

export default Relative
