import React from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation} from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation'

const ListProducts = ({title, dataset, theme, swiperButton}) => {

    const formatPrice = (price) => {
        // Sử dụng Intl.NumberFormat để định dạng số với dấu phân cách hàng nghìn
        const formattedPrice = new Intl.NumberFormat('vi-VN').format(price);
        return {formattedPrice};
    };

    return (
        <div className="listProducts">
            <div className="title-link">
                <div className="theme">
                    {theme}
                    <h3 className="title-section d-inline">{title}</h3>
                </div>
                <div className={`swiper-navigation swiper-navigation-custom ${swiperButton}-navigation`}>
                    <div className={`swiper-button-prev ${swiperButton}-prev`}>
                        <i className="fa-solid fa-angle-left"></i>               
                    </div>
                    <div className={`swiper-button-next ${swiperButton}-next`}>
                        <i className="fa-solid fa-angle-right"></i>
                    </div>
                </div>
                {/* {isSeeAll && <a href="">Xem tất cả</a>} */}
            </div>
            <Swiper
                modules={[Navigation]}
                spaceBetween={20}
                slidesPerView={4}
                navigation={{
                    prevEl: `.${swiperButton}-prev`,
                    nextEl: `.${swiperButton}-next`,
                }}
                breakpoints={{
                    0: { // Với mọi kích thước màn hình
                        slidesPerView: 2, // Điện thoại, chỉ hiện 1 slide
                    },
                    776: { // Màn hình từ 640px trở lên (tablet trở lên)
                        slidesPerView: 3, // Hiện 2 slide như mặc định
                    },
                    1032: {
                        slidesPerView: 4,
                    }
                    // 1200: {
                    //     slidesPerView:5,
                    // }
                }}
            >
            {dataset && dataset.map(data => (
                <SwiperSlide>
                    <div key={data.product_id} className="product-item wrp-effect-scale wrp-effect-boxshadow">
                        <div className="inner-image">
                            <div className="ratio-1-1">
                                <a href="" title={data.product_name}>
                                    <img className="img-fluid" src={data.product_image_url}/>
                                </a>
                            </div>
                        </div>
                        <div className="product-item__content">
                            <h4 className="product-name">
                                <a href="">
                                    {data.product_name}
                                </a>
                            </h4>
                            <div className="product-price">
                                <span className="price-amount">
                                    {formatPrice(data.price)}
                                    <span className="currency-symbol">VND</span>
                                </span>
                                {data.old_price && 
                                    <span className="price-amount old-price">
                                        {formatPrice(data.old_price)}
                                        <span className="currency-symbol">VND</span>
                                    </span>
                                }
                            </div>
                        </div>
                    </div>
                </SwiperSlide>
            ))}
            </Swiper>
        </div>
    )   
}

export default ListProducts
