import React, { useContext, useMemo } from "react";
import { FavoriteContext } from '../contexts/FavoriteContext';

const Product = ({ dataProducts }) => {
    const { favorites, addToFavorites, removeFromFavorites } = useContext(FavoriteContext);

    // Format price for display
    const formatPrice = (price) => {
        return new Intl.NumberFormat('vi-VN').format(price);
    };

    // Kiểm tra xem sản phẩm có trong danh sách yêu thích không, dùng useMemo để tối ưu hiệu suất
    const isFavorite = useMemo(() => favorites.includes(dataProducts.product_id), [favorites, dataProducts.product_id]);

    // Hàm xử lý sự kiện yêu thích sản phẩm
    const handleToggleFavorite = () => {
        if (isFavorite) {
            removeFromFavorites(dataProducts.product_id);
        } else {
            addToFavorites(dataProducts.product_id);
        }
    };

    // Tránh lỗi nếu dữ liệu sản phẩm không đầy đủ
    if (!dataProducts || !dataProducts.product_id || !dataProducts.product_name || !dataProducts.product_image_url) {
        return null; // Trả về null hoặc có thể hiển thị thông báo lỗi
    }

    return (
        <div className="product-item wrp-effect-scale wrp-effect-boxshadow">
            <div className="inner-image">
                <div className="ratio-1-1">
                    <a href={`/product/${dataProducts.product_id}`} title={dataProducts.product_name}>
                        <img className="img-fluid" src={dataProducts.product_image_url} alt={dataProducts.product_name} />
                    </a>
                </div>
            </div>
            <div className="product-item__content">
                <h4 className="product-name">
                    <a href={`/product/${dataProducts.product_id}`}>
                        {dataProducts.product_name}
                    </a>
                </h4>
                <div className="product-price">
                    <span className="price-amount">
                        {formatPrice(dataProducts.price)}
                        <span className="currency-symbol"> VND</span>
                    </span>
                    {dataProducts.old_price && (
                        <span className="price-amount old-price">
                            {formatPrice(dataProducts.old_price)}
                            <span className="currency-symbol"> VND</span>
                        </span>
                    )}
                </div>
                <div className="product-action">
                    <button 
                        className="favorites" 
                        onClick={handleToggleFavorite}
                        aria-label={isFavorite ? 'Xóa khỏi yêu thích' : 'Thêm vào yêu thích'} // Cải thiện khả năng truy cập
                    >
                        <i 
                            className={`fa-heart me-2 ${isFavorite ? 'fa-solid' : 'fa-regular'}`} 
                            style={{ color: isFavorite ? '#FF4D4D' : 'inherit' }}
                        ></i>
                        {isFavorite ? "Đã thích" : "Thích"}
                    </button>
                    <div className="buy-now">
                        <i className="fa-solid fa-basket-shopping"></i>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Product;
