import React, { createContext, useState, useEffect, useContext } from 'react';
import { AuthContext } from "../contexts/AuthContext";
import axios from 'axios';

export const FavoriteContext = createContext();

export const FavoriteProvider = ({ children }) => {
    const { user } = useContext(AuthContext);
    const [favorites, setFavorites] = useState([]);

    const storageKey = user ? `favorites_${user.id}` : 'favorites_guest';

    // Hàm tải danh sách yêu thích từ database hoặc localStorage
    const loadFavorites = async () => {
        if (user) {
            try {
                const response = await axios.post(`http://localhost:3000/favorites`, { userId: user.user_id });
                setFavorites(response.data);
            } catch (error) {
                console.error("Error loading favorites from database:", error);
            }
        } else {
            const savedFavorites = JSON.parse(localStorage.getItem(storageKey)) || [];
            setFavorites(savedFavorites);
        }
    };

    useEffect(() => {
        loadFavorites();
    }, [user]);

    // Lưu danh sách yêu thích vào localStorage mỗi khi thay đổi nếu không có user
    useEffect(() => {
        if (!user) {
            localStorage.setItem(storageKey, JSON.stringify(favorites));
        }
    }, [favorites, user, storageKey]);

    const addToFavorites = async (product_id) => {
        if (!favorites.find(item => item === product_id)) {
            const newFavorites = [...favorites, { product_id }];
            setFavorites(newFavorites);

            if (user) {
                try {
                    await axios.post('http://localhost:3000/favorites', {
                        userId: user.user_id,
                        product_id: product_id
                    });
                } catch (error) {
                    console.error("Error saving favorite to database:", error);
                }
            } else {
                localStorage.setItem(storageKey, JSON.stringify(newFavorites));
            }
        }
    };

    const removeFromFavorites = async (product_id) => {
        const newFavorites = favorites.filter(item => item !== product_id);
        setFavorites(newFavorites);

        if (user) {
            try {
                await axios.delete(`http://localhost:3000/favorites`, {
                    data: { userId: user.user_id, product_id }
                });
            } catch (error) {
                console.error("Error removing favorite from database:", error);
            }
        } else {
            localStorage.setItem(storageKey, JSON.stringify(newFavorites));
        }
    };

    return (
        <FavoriteContext.Provider value={{ favorites, addToFavorites, removeFromFavorites }}>
            {children}
        </FavoriteContext.Provider>
    );
};
