import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext();  // Sử dụng thông tin đăng nhập chia sẻ AuthProvider

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Lấy thông tin người dùng từ localStorage khi ứng dụng tải lần đầu
        try {
            const storedUser = localStorage.getItem('user');
            if (storedUser) {
                setUser(JSON.parse(storedUser));  // Chỉ parse khi có dữ liệu
            }
        } catch (error) {
            console.error('Error parsing user data from localStorage:', error);
        }
    }, []);

    const login = (userData) => {
        if (userData && userData.id) {  // Kiểm tra dữ liệu hợp lệ
            localStorage.setItem('user', JSON.stringify(userData));
            setUser(userData);
        } else {
            console.error('Invalid user data:', userData);
        }
    };

    const logout = () => {
        // Xóa dữ liệu khi người dùng đăng xuất
        localStorage.removeItem('user');
        setUser(null);
    };

    return (
        <AuthContext.Provider value={{ user, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
